    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarActionPerformed
        String termo = textPesquisar.getText();
        
        if (termo.isEmpty()) {
            JOptionPane.showMessageDialog(null, "O campo de pesquisa está vázio", "Aviso", JOptionPane.WARNING_MESSAGE);
        } else {
            model.addSearchRow(termo);
        }
    }//GEN-LAST:event_btnPesquisarActionPerformed

