    private void labelStatusAncestorAdded(javax.swing.event.AncestorEvent evt) {//GEN-FIRST:event_labelStatusAncestorAdded
        String status = new AtualizarStatus().statusProduto();
        labelStatus.setText(status);
    }//GEN-LAST:event_labelStatusAncestorAdded

    private void comboBoxCategoriaItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_comboBoxCategoriaItemStateChanged
        if (evt.getStateChange() == ItemEvent.SELECTED) {
            Categoria dadosCategoria = (Categoria) comboBoxCategoria.getSelectedItem();
            int id = dadosCategoria.getId_categoria();
            model.addCategoriaRow(id);
        }
    }//GEN-LAST:event_comboBoxCategoriaItemStateChanged

    private void comboBoxCategoriaAncestorAdded(javax.swing.event.AncestorEvent evt) {//GEN-FIRST:event_comboBoxCategoriaAncestorAdded
        ClasseComboBox cb = new ClasseComboBox();
        List<Categoria> itens = cb.comboCategoria();
        comboBoxCategoria.removeAll();
        itens.forEach((c) -> {
            comboBoxCategoria.addItem(c);
        });
    }//GEN-LAST:event_comboBoxCategoriaAncestorAdded

    private void comboBoxFornecedorAncestorAdded(javax.swing.event.AncestorEvent evt) {//GEN-FIRST:event_comboBoxFornecedorAncestorAdded
        ClasseComboBox cb = new ClasseComboBox();
        List<Fornecedor> itens = cb.comboFornecedor();
        comboBoxFornecedor.removeAll();
        itens.forEach((f) -> {
            comboBoxFornecedor.addItem(f);
        });
    }//GEN-LAST:event_comboBoxFornecedorAncestorAdded

    private void tableAncestorAdded(javax.swing.event.AncestorEvent evt) {//GEN-FIRST:event_tableAncestorAdded
        table.setModel(model);
        model.addRow();
    }//GEN-LAST:event_tableAncestorAdded
    

