    private void comboExibirItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_comboExibirItemStateChanged
        if (evt.getStateChange() == ItemEvent.SELECTED) {
            String valor = String.valueOf(comboExibir.getSelectedItem());
            model.addRowPorTempo(valor);
        }
    }//GEN-LAST:event_comboExibirItemStateChanged
    
